const balls=document.getElementsByClassName("ball"),presentationContainer=document.querySelector(".presentation-container");function moveEyes(e){let x=100*e.clientX/window.innerWidth+"%",y=100*e.clientY/window.innerHeight+"%";for(let i=0;i<2;i++)balls[i].style.left=x,balls[i].style.top=y,balls[i].style.transform="translate(-"+x+",-"+y+")"}presentationContainer.addEventListener("mousemove",e=>moveEyes(e));